import React, { useState, useEffect } from 'react';
import { Link, navigate } from '@reach/router';
import axios from 'axios';

const EditPet = (props) => {
    const [formInfo, setFormInfo] = useState({
        tname: "",
        type: "",
        desc: "",
        skillA: "",
        skillB: "",
        skillC: ""
    })

    useEffect(()=>{
        axios.get(`http://localhost:8000/api/things/${props.petId}`)
        .then(res => {
            console.log('************')
            console.log(res)
            console.log('************')
            setFormInfo(res.data.results)
        })
        .catch(err=> console.log (err))

    }, []);




    const changeHandler = (e) =>{
        console.log("********CHANGING********")
        setFormInfo({
            ...formInfo,
            [e.target.name]:e.target.value
        })
    }

    const submitHandler = (e) =>{
        e.preventDefault()
        console.log("getting ready to submit, please work.", formInfo)
        axios.put(`http://localhost:8000/api/thingas/update/${props.prodId}`, formInfo)
            .then(res=>{
                console.log('***********')
                console.log(res)
                navigate(`/products/${props.prodId}`)
            })
            .catch(err=> console.log (err))
    }






    return (
        <div>
            <br />
            <Link to="/">Back to Home</Link>
            <br />
            <br />
            <h3>Edit {formInfo.name} </h3>
        </div>
    );
};

export default EditPet;
