import React, { useState, useEffect } from 'react';
import { Link } from '@reach/router';
import axios from 'axios'

const ShowAll = () => {
    const [allPets, setAllPets] = useState ([])

    const [deleteClicked, setDeleteClicked] = useState(false)

    useEffect(()=>{
        axios.get("http://localhost:8000/api/things")
        .then(res => {
            console.log('*************')
            console.log(res)
            console.log('*************')
            setAllPets(res.data.results)
        })
        .catch()

    }, [deleteClicked]);


    return (
        <div>
            <div className="container">
                <Link to="/pets/new">Add a Pet to the Shelter.</Link>
                <h3>These Pets are looking for a good home.</h3>
                <table class="table table-striped">
                    <thead>
                        <tr>
                            <th scope="col">Name</th>
                            <th scope="col">Type</th>
                            <th scope="col">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                    {
                allPets.map((product, idx)=>{
                    return <div key={idx}>
                        <Link style={{fontWeight: "bold", fontSize: "40px"}} to={`/products/${product._id}`}>{product.title}</Link>
                        <br/>
                        <button onClick={(e)=>deleteProd(e, product._id)} >Delete Product</button>
                        <button><Link to={`/edit/${product._id}`}>Edit Product</Link></button>
                        <br/>
                        <br/>
                    </div>
                })
            }



                        <tr>
                            <th scope="row">Happy</th>
                            <td>Dog</td>
                            <td><Link to="/pets/id">details</Link> | <Link to="/pets/id/edit">edit</Link></td>
                        </tr>



                    </tbody>
                </table>
            </div>
        </div>
    );
};


export default ShowAll;
